function updateFilePreviewModal(e) {
    (document.querySelector("#fileModalTitle").innerHTML = e.name),
        (document.querySelector("#fileModalTitle").title = e.name),
        (document.querySelector("#fileSizeFormatted").innerHTML = e.sizeFormatted),
        (document.querySelector("#fileExtension").innerHTML = e.extension),
        (document.querySelector("#fileType").innerHTML = e.type),
        document.querySelector("#fileDeleteBtn").setAttribute("onclick", "deleteFile('" + e.slugName + "')"),
        "image" == e.type ? (document.querySelector("#fileModalImage").src = "/storage/files/original/" + e.slugName) :
            (document.querySelector("#fileModalImage").src = "/storage/extensionImages/" + e.slugName.split(".")[1] + ".png");
}
function deleteFile(e) {
    const t = document.querySelector('meta[name="csrf-token"]').content;
    fetch("api/files/delete", {
        method: "delete",
        credentials: "same-origin",
        body: JSON.stringify({ slug: e }),
        headers: {
            "Content-Type": "application/json",
            "X-CSRF-Token": t,
            Accept: "application/json, text-plain, */*",
            "X-Requested-With": "XMLHttpRequest",
        },
    })
        .then((e) => e.json())
        .then((e) => {
            console.log(e),
                1 == e.success
                    ? (document
                        .querySelector("#container-" + e.data.selector).remove(),
                        document.querySelector("#fileModalCloseBtn").click(),
                        notify("Message", e.message, "", "ci-security-check", "success")) : 0 == e.success && notify(
                            "Error Message", e.message, "", "ci-security-prohibition", "danger");
        })
        .catch((e) => {
            console.log(e), notify("Error Message", e.message, "", "ci-security-prohibition", "danger");
        });
}
